@font-face {
    font-family: 'icomoon';
    src: url('icomoon.woff') format('woff');
    font-weight: normal;
    font-style: normal;
    font-display: block;
}

*,
 ::before,
 ::after {
    box-sizing: border-box;
    margin: 0;
    padding: 0;
}

body {
    font-family: sans-serif;
    color: #333;
}

.icon-heart {
    text-decoration: none;
}

.icon-heart::before {
    font-family: icomoon;
    content: '\e901';
}

.icon-cart::before {
    font-family: icomoon;
    content: '\e93a';
}

.container {
    max-width: 900px;
    margin: 0 auto;
    padding: 0 15px;
}

.header {
    background-color: lightblue;
    padding: 20px 0;
}

.header__container {
    display: flex;
    align-items: center;
}

.navigation {
    margin-right: auto;
}

.navigation__list {
    list-style: none;
}

.navigation__link {
    text-decoration: none;
    color: inherit;
    font-weight: 600;
    transition: color 0.3s;
}

.navigation__link:hover {
    color: red;
}

.cart-btn {
    display: flex;
    align-items: center;
    background: none;
    border: none;
    outline: none;
    color: inherit;
    font-size: inherit;
    cursor: pointer;
    transition: color 0.3s;
}

.cart-btn::before {
    margin-right: 10px;
    font-size: 20px;
    color: green;
}

.cart-btn:hover {
    color: green;
}

.favorite {
    margin-left: 20px;
}

.favorite__link {
    list-style: none;
    color: #333;
}

.favorite__link:hover {
    color: red;
}

.favorite__link::before {
    margin-right: 10px;
    font-size: 20px;
    color: red;
}

.products {
    display: grid;
    list-style: none;
    grid-template-columns: repeat(3, 1fr);
    grid-gap: 40px;
    padding: 40px 0;
}

.products__item {}

.card {
    position: relative;
    display: flex;
    flex-direction: column;
    box-shadow: 0 3px 15px rgba(200, 200, 200, 0.4);
    border-radius: 10px;
}

.card__header {
    padding: 30px;
    margin-bottom: auto;
}

.card__body {
    display: flex;
    flex-direction: column;
    padding: 0 20px;
}

.card__img {
    display: flex;
    width: 100%;
}

.card__name {
    font-size: 12px;
    font-weight: 300;
    letter-spacing: 0.5px;
    line-height: 1.5;
}

.card__footer {
    display: flex;
    align-items: center;
    justify-content: space-between;
    padding: 20px;
}

.card__price {
    margin-top: auto;
    font-size: 18px;
    font-weight: 900;
}

.card__btn {
    display: block;
    width: 90px;
    padding: 5px 10px;

    color: rgba(0, 0, 0, .6);
    text-decoration: none;
    text-align: center;
    font-size: 13px;
    font-family: inherit;
    font-weight: 700;

    background: #eaeef1;
    background-image: linear-gradient(rgba(0, 0, 0, 0), rgba(0, 0, 0, .1));
    border-radius: 5px;

    text-shadow: 0 1px 1px rgba(255, 255, 255, .7);
    box-shadow: 0 0 0 1px rgba(0, 0, 0, .2), 0 1px 2px rgba(0, 0, 0, .2), inset 0 1px 2px rgba(255, 255, 255, .7);
}

.card__btn:hover {
    background: #fff;
}

.card__heart {
    position: absolute;
    right: 0;
    top: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    width: 40px;
    height: 40px;
    background: none;
    border: none;
    outline: none;
    cursor: pointer;
    font-size: 24px;
    color: red;
}

.card__heart::before {
    font-family: icomoon;
    content: '\e900';
}

.card__heart._active::before {
    content: '\e901';
}

.cart {
    display: flex;
    justify-content: center;
    align-items: center;
    width: 100%;
    height: 100%;
    background-color: rgba(0, 0, 0, 0.5);
    position: fixed;
    top: 0;
    left: 0;
    z-index: 100;
    visibility: hidden;
}

.cart._active {
    visibility: visible;
}

.cart__dialog {
    position: relative;
    width: calc(100% - 40px);
    max-width: 700px;
    min-height: calc(100% - 40px);
    padding: 40px 30px 30px;
    background-color: #fff;
    border-radius: 5px;
    overflow: hidden;
}

.cart__title {
    margin-bottom: 50px;
    font-size: 28px;
    font-weight: 600;
}

.cart__products {
    display: grid;
    grid-template-columns: repeat(2, 1fr);
    grid-gap: 20px;
    list-style: none;
}

.cart__product {
    display: flex;
}

.cart__product img {
    width: 70px;
    height: 70px;
    object-fit: contain;
    object-position: center;
}

.cart__product h3 {
    font-size: 18px;
    font-weight: 600;
}

.cart__close {
    position: absolute;
    right: 0;
    top: 0;
    display: flex;
    justify-content: center;
    align-items: center;
    width: 40px;
    height: 40px;
    background: none;
    border: none;
    outline: none;
    cursor: pointer;
    font-size: 24px;
}

.cart__total-cost {
    display: flex;
    align-items: center;
    margin: 0 -30px;
    padding: 0 30px;
    width: 100%;
    height: 70px;
    border-top: 1px solid #ccc;
    position: absolute;
    left: 0;
    bottom: 0;
}

.cart__total-cost b {
    margin-right: 7px;
    padding-left: 10px;
}
